docinfo.latex-image-preamble:
\usepackage{tikz}

\usepackage{smartdiagram}           % for a circular diagram
\pgfplotsset{axis x line = middle,
             axis y line = middle}
\usetikzlibrary{backgrounds}
\usetikzlibrary{arrows,matrix}
\usetikzlibrary{positioning}        % for Dave R's worksheet
\usepackage{circuitikz}             % for Virgil P's worksheet

image.asymptote:
import graph3;
import solids;
size(0,200);
currentprojection=perspective(1.5,0,10,Y);
pen color=lightblue+opacity(0.55);

real f(real x){return sqrt(x);}
pair F(real x){return (x,f(x));}
triple F3(real x){return (x,f(x),0);}

path p=graph(F,0,1,n=20,operator ..);
path3 p3=path3(p);

revolution a=revolution(p3,X,0,360);
draw(surface(a),color,render(compression=Low,merge=true));
draw(p3,blue);

real x=relpoint(p,0.5).x;

xaxis3(Label("$x$",1),xmax=1.5,dashed,Arrow3);
yaxis3(Label("$y$",1),Arrow3);
dot(Label("$(1,1)$"),(1,1,0));
arrow(Label("$y=\sqrt{x}$"),F3(0.7),Y,0.75cm,red);
draw(arc(1.2X,0.4,90,90,175,-40,CW),Arrow3);
draw("$r$",(x,0,0)--F3(x),red,Arrow3,PenMargin3);

import graph3;
import solids;
size(300,200);
//currentprojection=perspective(1.5,0,10,Y);
currentprojection=perspective(-1.5,0,10,Y);
pen color=lightblue+opacity(0.55);

real f(real x){return x^3;}
pair F(real x){return (x,f(x));}
triple F3(real x){return (x,f(x),0);}

path p=graph(F,0,2,n=20,operator ..);
path3 p3=path3(p);

revolution a=revolution(p3,Y,0,360);
draw(surface(a),color,render(compression=0,merge=true));
draw(p3,blue);

real x=relpoint( p,1).x;
xaxis3(Label("$x$",1),xmax=2,Arrow3);
yaxis3(Label("$y$",1),ymin=-0.5,ymax=10,dashed,Arrow3);
dot(Label("$(2,8)$"),(2,8,0),X);
draw(arc(9Y,1,90,0,7.5,180),Arrow3);   
arrow(Label("$y=x^3$"),(1.5, 3,0),X,0.75cm,red);    
draw("$r$",(0,f(1.7),0)--F3(1.7),red,Arrow3,PenMargin3);

mport graph3;
import solids;
size(0,200);
currentprojection=perspective(2,0,10,up=Y);
pen color=lightblue+opacity(0.25);
pen color=lightblue+opacity(0.35);
real alpha=360;

real f(real x) {return x^2;}
pair F(real x) {return (x,f(x));}
triple F3(real x) {return (x,f(x),0);}

path p=graph(F,0,1,n=10,operator ..)--cycle;
path3 p3=path3(p);

revolution a=revolution(p3,X,-alpha,0);
render render=render(compression=0,merge=true);
draw(surface(a),color,render);
surface s=surface(p);
draw(s,color,render);
draw(rotate(-alpha,X)*s,color,render);

draw(p3, red);

xaxis3(Label("$x$",1),xmax=1.25,dashed,Arrow3);
yaxis3(Label("$y$",1),Arrow3);
dot(Label("$(1,1)$"),(1,1,0),X+Y);
arrow("$y=x$",(0.7,0.7,0),Y,0.75cm,red);
arrow("$y=x^2$",F3(0.7),X,0.75cm,red);
draw(arc(1.1X,0.3,90,90,3,-90),Arrow3);
